//#region 壁纸弹窗
var PopupModal = (function () {
    var getPicUrl = null;
    var getDownloadUrl = null;
    var getCDNDomain = null;
    var cacheData = null;

    var PopupModal = function () {
        this.init = function (config) {
			var self = this;
            config = config || {};
            this.getPicUrl = config.getPicUrl;
            this.getDownloadUrl = config.getDownloadUrl;
            this.getCDNDomain = config.getCDNDomain;

            $("#primaryImage").load(function () {
                //alert("加载完成!");
                $("#loadingImage").hide();
                $("#primaryImage").show();
            });

            $('#popupWindow').jqm({ modal: true });
            $('#popupWindow').jqmAddTrigger($("#popup")); //弹出窗口
            $('#popupWindow').jqmAddClose("#closePopup");  //关闭按钮
			$("#popupRecommandPic").on('click', 'li a', function () {
                self.show($(this).attr('pic_id'));
            });
        };
        this.show = function (id, resolution, platform) {
            var self = this;
            $("#primaryImage").hide();
            $("#loadingImage").show();
            var size = 3;
            var url = self.getPicUrl + "?resolution=" + resolution + "&pic_id=" + id + "&f_id=" + id + "&size=" + size + "&callback=?";
            // 设置下载链接
            $(".icon_btn a:eq(0)").click(function () {
                $("#hiddenIFrame").attr("src", self.getDownloadUrl + "?platform=" + platform + "&resolution=" + "resolution&pic_id=" + id + "&f_id=" + id);
            });
            // 获取预览图片
            $.getJSON(url, function (data) {
                if (data != null && data.list && data.list.length > 0) {
                    cacheData = data;
                    self.bindData();
                }
            });
        };
        this.bindData = function () {
            var self = this;
            var cdnDomain = self.getCDNDomain;
            data = cacheData;
            if (data != null && data.list && data.list.length > 0) {
                var pic_diggnum = data.pic_diggnum;
                var main_id = data.list[0].pic_id;
                var mainname = data.list[0].pic_title;
                var imgsrc = "http://" + cdnDomain + "/" + data.list[0].pic_fname;
                var license = data.list[0].pic_license;

                //特殊图片格式
                var imglength = imgsrc.length;
                var bigimg = imgsrc;
                imgsrc = imgsrc.substring(0, imglength - 4) + "_3x60" + imgsrc.substring(imglength - 4, imgsrc.length);

                //左侧预览图优先使用特殊的格式,如不存在则使用大图
                $("#primaryImage")[0].onerror = function () {
                    $("#primaryImage").attr("src", bigimg);
                };
                $("#primaryImage").attr("src", imgsrc);
                $("#primaryImage").attr("alt", mainname);
                $("#primaryImage").attr("f_id", main_id);

                if (parseInt(data.w) > parseInt(data.h)) {
                    $("#popupRecommandPic").attr("class", "p2");
                    $("#popupLeft").attr("class", "p2");
                    $("#popupLayer").attr("class", "b_layer");
                    $("#primaryImage").css("width", "346px");
                    try {
                        //277 :315
                        if (data.h > 277) {
                            $("#primaryImage").height(277);
                        }
                    }
                    catch (exxx) { }
                }
                else {
                    $("#popupRecommandPic").attr("class", "");
                    $("#popupLeft").attr("class", "");
                    $("#popupLayer").attr("class", "layer");
                    $("#primaryImage").css("width", "246px");
                    try {
                        //369 :420 
                        if (data.h > 369) {
                            $("#primaryImage").height(369);
                        }
                    } catch (exx) { }
                }
                $("#primaryName").text(mainname);

                //右侧推荐图片
                $("#popupRecommandPic")[0].innerHTML = "";
                for (var i = 1, len = data.list.length; i < len; i++) {
                    var pic_id = data.list[i].pic_id;
                    var name = data.list[i].pic_title;
                    var imgsrc = "http://" + cdnDomain + "/" + data.list[i].pic_fname;
                    var license = data.list[i].pic_license;
                    var pic_103x77_resolution = "http://" + cdnDomain + "/" + data.list[i].pic_103x77_resolution;
                    var pic_118x178_resolution = "http://" + cdnDomain + "/" + data.list[i].pic_118x178_resolution;
                    $("#popupRecommandPic").append(" <li><a style='cursor:pointer;' pic_id='" + pic_id + "'  f_id='" + pic_id + "' data-index='" + i + "'><img   src='" + pic_118x178_resolution + "'/><span>" + name + "</span></a></li>");
                }

                $("#popupWindow").jqmShow();
            }
        }
    };
    return PopupModal;
})();

