//#region jQuery ready
$(document).ready(function () {
    jQuery("#dialog").css("z-index", "10000");
    //placeholder
    $('[placeholder]').live({
        'focus': function () {
            var input = $(this);
            if (input.val() == input.attr('placeholder')) {
                input.val('');
            }
        },
        'blur': function () {
            var input = $(this);
            if (input.val() == '' || input.val() == input.attr('placeholder')) {
                input.val(input.attr('placeholder'));
            }
        }
    }).blur();
});
