//#region 搜索条件
var PicCondition = (function () {
    var cid, platform, resolution, order, keyword, page, pagesize; //static var
    var PicCondition = function (config) {
        config = config || {};
        this.cid = config.cid || this.cid || '';  //null时保留原值,''清空;
        this.platform = config.platform || this.platform || '';  //null时保留原值,''清空;
        this.resolution = config.resolution || this.resolution || '';  //null时保留原值,''清空;
        this.order = config.order || this.order || '';  //null时保留原值
        this.keyword = config.keyword || '';
        this.page = config.page || 1;
        this.pagesize = config.pagesize || '';
    };
    return PicCondition;
})();
//#endregion

//#region 壁纸列表
var PicPage = {
    getListUrl: null,
    condition: null,
    //初始化
    init: function (obj) {
        this.getListUrl = obj.getListUrl;
        //请求URL中查询参数是否存在,否则使用默认列表
        var queryParams = $.parseParams(document.location.search);
        if (!$.isEmptyObject(queryParams)) {
            this.condition = new PicCondition(queryParams);
        } else {
            this.condition = new PicCondition(obj.defaultCondition);
        };
        this.loadData();
    },
    //加载服务端数据
    loadData: function (params) {
        $.ajax({
            type: 'GET',
            url: PicPage.getListUrl,
            dataType: "json",
            data: params || PicPage.condition,
            cache: true,
            success: PicPage.bindData
        });
    },
    //绑定数据,呈现列表
    bindData: function (json) {
        //无数据
        if (!json || !json.data || json.data.length == 0) {
            $('.wall_index_con').html('<div class="emptyData">很抱歉，暂无数据。</div>');
            return;
        }

        //列表呈现
        var resolution = json.resolution;
        var platform = json.platform;
        var total = json.total;
        var page = json.page;
        var pagesize = json.pagesize;
        var width = json.width;
        var height = json.height;
        var data = json.data;

        var ul = document.createElement('ul');
        ul.className = 'clearfix';
        ul.id = 'pic_list';
        $.each(data, function (i, e) {
            //li
            var li = document.createElement('li');
            //h1
            var h1 = document.createElement('h1');
            h1.innerHTML = e.pic_title;
            li.appendChild(h1);
            //p
            var p = document.createElement('p');
            //a
            var a = document.createElement('a');
            a.style.cursor = 'pointer';
            a.className = 'btn_preview';
            a.setAttribute('data-pic-id', e.pic_id);
            //img
            var img = document.createElement('img');
            img.width = width;
            img.height = height;
            img.alt = img.title = e.pic_title;
            img.setAttribute('onload', 'DrawImage(this, 120, 180)');
            img.setAttribute('src', e.pic_url);
            a.appendChild(img);
            p.appendChild(a);
            li.appendChild(p);
            //btn1
            var btn1 = document.createElement('a');
            btn1.href = e.pic_download_url;
            btn1.target = 'hiddenIFrame';
            btn1.innerHTML = '下载';
            btn1.className = 'down_linkbtn';
            li.appendChild(btn1);
            //btn2
            var btn2 = document.createElement('a');
            btn2.href = e.pic_detail_url;
            btn2.innerHTML = '详细';
            btn2.className = 'view_linkbtn';
            li.appendChild(btn2);
            ul.appendChild(li);
        });
        $('.wall_index_con').html(ul).append('<div class="page" id="Pager"></div>');
        $('.wall_index_con .btn_preview').click(function () {
            var id = $(this).attr('data-pic-id');
            pm.show(id, resolution, platform);
            return false;
        });
        //绑定分页栏控件
        $('#Pager').zzPagination(total, {
            currentPage: page - 1,
            jumpLinkButtonImage: 'http://img3.91huo.cn/zn.sj/mini/bz/0905/default/pagebtn1.gif',
            startEdgeCount: 1,
            perPageCount: pagesize,
            needPageNumber: false,
            callbackEvent: function (pageIndex) {
                PicPage.condition.page = pageIndex + 1;
                PicPage.loadData();
            }
        });
    }
}
//#endregion

