//#region 壁纸详情
var PicDetailPage = {
    getUrl: null,
    getDiggBuryUrl: null,
    urlReferrer: null,
    condition: null,
    //页面初始化
    init: function (obj) {
        this.getUrl = obj.getUrl;
        this.getDiggBuryUrl = obj.getDiggBuryUrl;
        this.urlReferrer = obj.urlReferrer;
        //请求URL中查询参数是否存在,否则使用默认列表
        var queryParams = $.parseParams(document.location.search);
        if (!$.isEmptyObject(queryParams)) {
            this.condition = queryParams;
        } else {
            this.condition = obj.defaultCondition;
        };
        this.loadData();
    },
    //献花
    diggBury: function (type, platform, id, name, el) {
        $.ajax({
            type: "GET",
            url: PicDetailPage.getDiggBuryUrl,
            data: { platform: platform, type: type, pic_id: id, pic_name: name },
            async: false,
            cache: false,
            success: function (data) {
                if (data && data.status == 1) {
                    $.msg({ cssInner: true, content: data.msg });
                    var diggNumber = parseInt($(el).text(), 10) + 1;
                    $(el).text(diggNumber);
                    var name = "pic_dig_" + id;
                    var value = diggNumber;
                    var exp = new Date();
                    exp.setTime(exp.getTime() + 50 * 60 * 1000);
                    document.cookie = name + "=" + escape(value) + "; domain=sj.91.com; expires=" + exp.toGMTString() + ";path=/";
                }
                else {
                    $.msg({ cssInner: true, content: '您已经献过花了！' });
                }
            }
        });
    },
    //加载服务端数据
    loadData: function (params) {
        $.ajax({
            type: 'GET',
            url: PicDetailPage.getUrl,
            dataType: "json",
            data: params || PicDetailPage.condition,
            cache: true,
            success: PicDetailPage.bindData
        });
    },
    //绑定数据,呈现列表
    bindData: function (json) {
        //无数据
        if (!json) {
            $('.wall_info_con1').html('<div class="emptyData">很抱歉，暂无数据。</div>');
            return;
        } else if (json.status == 0 && json.msg) { //错误提示
            $('.wall_info_con1').html('<div class="emptyData">' + json.msg + '</div>');
            return;
        };

        //数据呈现
        var resolution = json.resolution;
        var platform = json.platform;
        var template =
            "<div class='wall_info_title'><span>手机壁纸</span> </div>" +
            "<div class='clearfix'></div>" +
            "<p>" +
                "<a onclick='{0}'>" +
                    "<img src='{1}' alt='{2}' title='{2}' />" +
                "</a>" +
            "</p>" +
            "<div class='wall_info_con1_c'>" +
                "<h1>图片名称：{3}</h1>" +
                "<h2>关注度：{4}</h2>" +
                "<h2>人气指数：{5}</h2>" +
                "<h2>鲜　　花：<span>{6}</span>&nbsp;<a id='btnDigg' onclick='{7}'>我来献花</a></h2>" +
                "<h5> <a href='{8}' target='hiddenIFrame' >下载</a> </h5>" +
                "<h6><a style='cursor:pointer;' onclick='{9}' target='hiddenIFrame' >预览</a></h6>" +
                "<h3><a href='{10}'>&lt;&lt;返回上一页</a></h3>" +
            "</div>";
        var html = String.format(template,
                 'pm.show(' + json.pic_id + ',"' + resolution + '","' + platform + '")',
                 json.pic_img_url,
                 json.pic_title,
                 json.pic_title,
                 json.pic_hotindeximg_url,
                 json.pic_viewnum,
                 json.pic_diggnum,
                 '',
                 json.pic_download_url,
                 'pm.show(' + json.pic_id + ',"' + resolution + '","' + platform + '")',
                 PicDetailPage.urlReferrer
            );
        $('.wall_info_con1').html(html);
        $('#btnDigg').attr('onclick', "PicDetailPage.diggBury('digg', '" + platform + "'," + json.pic_id + ",'" + json.pic_title + "', this.previousSibling.previousSibling);");
    }
};
//#endregion

